        }

        .app-btn-text {
            text-align: left;
        }

        .app-btn-small {
            font-size: 11px;
            opacity: 0.7;
            font-weight: 500;
        }

        .app-btn-large {
            font-size: 18px;
            font-weight: 700;
        }

        /* Footer */
        footer {
            background: var(--gradient-dark);
            color: white;
            padding: 100px 0 40px;
