        }

        .footer-brand p {
            color: rgba(255,255,255,0.6);
            margin-top: 24px;
            font-size: 15px;
            line-height: 1.8;
        }

        .footer-tagline {
            color: var(--sage);
            font-size: 13px;
            margin-top: 12px;
            letter-spacing: 2px;
            font-weight: 600;
        }

        .footer-title {
            font-size: 14px;
            font-weight: 700;
            margin-bottom: 28px;
            text-transform: uppercase;
            letter-spacing: 2px;
        }

        .footer-links {
