        /* Responsive */
        @media (max-width: 1200px) {
            .hero-content h1 {
                font-size: 52px;
            }

            .section-title {
                font-size: 44px;
            }
        }

        @media (max-width: 1024px) {
            .hero-container,
            .sustainability-container {
                grid-template-columns: 1fr;
                gap: 60px;
            }

            .hero-content h1 {
                font-size: 44px;
            }

            .hero-visual {
                order: -1;
            }

            .hero-logo-large {
                max-width: 280px;
            }

            .steps-grid {
                grid-template-columns: repeat(2, 1fr);
            }

            .features-grid {
                grid-template-columns: repeat(2, 1fr);
            }

            .team-grid {
                grid-template-columns: repeat(2, 1fr);
            }

            .pricing-grid {
                grid-template-columns: 1fr;
                max-width: 420px;
                margin-left: auto;
                margin-right: auto;
            }

            .pricing-card.featured {
                transform: none;
            }

            .pricing-card.featured:hover {
                transform: translateY(-12px);
            }

            .footer-top {
                grid-template-columns: 1fr 1fr;
                gap: 50px;
            }
        }

        @media (max-width: 768px) {
            .nav-links {
                display: none;
            }

            .nav-container {
                padding: 0 24px;
            }

            .hero {
                padding: 120px 24px 80px;
            }

            .hero-content h1 {
                font-size: 36px;
                letter-spacing: -1px;
            }

            .hero-content p {
                font-size: 17px;
            }

            .section-container {
                padding: 0 24px;
            }

            .steps-grid,
            .features-grid,
            .team-grid {
                grid-template-columns: 1fr;
            }

            .hero-buttons {
                flex-direction: column;
            }

            .hero-stats {
                flex-direction: column;
                gap: 24px;
            }

            .stat::after {
                display: none;
            }

            .section-title {
                font-size: 36px;
            }

            .sustainability-content h2 {
                font-size: 40px;
            }

            .footer-top {
                grid-template-columns: 1fr;
                gap: 40px;
            }

            .footer-bottom {
                flex-direction: column;
                gap: 24px;
                text-align: center;
            }

            .app-buttons {
                flex-direction: column;
                align-items: center;
            }
        }

        /* ============================================
           SPLASH SCREEN / LOADING ANIMATION
           ============================================ */
        .splash-screen {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: linear-gradient(180deg, #E8F4F8 0%, #FDFCFA 50%, #F5E6D3 100%);
            z-index: 10000;
            display: flex;
            flex-direction: column;
            justify-content: center;
            align-items: center;
            transition: opacity 0.8s ease, visibility 0.8s ease;
            overflow: hidden;
        }

        .splash-screen.hidden {
            opacity: 0;
            visibility: hidden;
            pointer-events: none;
        }

        /* Animated Clouds */
        .splash-clouds {
            position: absolute;
            width: 100%;
            height: 100%;
            overflow: hidden;
            pointer-events: none;
        }

        .cloud {
            position: absolute;
            background: white;
            border-radius: 100px;
            opacity: 0.7;
            filter: blur(2px);
        }

        .cloud::before,
        .cloud::after {
            content: '';
            position: absolute;
            background: white;
            border-radius: 50%;
        }

        .cloud-1 {
            width: 200px;
            height: 60px;
            top: 15%;
            left: -200px;
            animation: cloudMove 15s linear infinite;
        }
        .cloud-1::before { width: 80px; height: 80px; top: -40px; left: 30px; }
        .cloud-1::after { width: 100px; height: 100px; top: -50px; left: 80px; }

        .cloud-2 {
            width: 160px;
            height: 50px;
            top: 30%;
            left: -160px;
            animation: cloudMove 20s linear infinite 3s;
            opacity: 0.5;
        }
        .cloud-2::before { width: 60px; height: 60px; top: -30px; left: 20px; }
        .cloud-2::after { width: 80px; height: 80px; top: -40px; left: 60px; }

        .cloud-3 {
            width: 180px;
            height: 55px;
            top: 60%;
            left: -180px;
            animation: cloudMove 18s linear infinite 6s;
            opacity: 0.6;
        }
        .cloud-3::before { width: 70px; height: 70px; top: -35px; left: 25px; }
        .cloud-3::after { width: 90px; height: 90px; top: -45px; left: 70px; }

        .cloud-4 {
            width: 140px;
            height: 45px;
            top: 75%;
            left: -140px;
            animation: cloudMove 22s linear infinite 9s;
            opacity: 0.4;
        }
        .cloud-4::before { width: 50px; height: 50px; top: -25px; left: 15px; }
        .cloud-4::after { width: 70px; height: 70px; top: -35px; left: 50px; }

        @keyframes cloudMove {
            0% { transform: translateX(0); }
            100% { transform: translateX(calc(100vw + 300px)); }
        }

        /* Splash Content */
        .splash-content {
            position: relative;
            z-index: 2;
            text-align: center;
            animation: splashFadeIn 1s ease-out;
        }

        @keyframes splashFadeIn {
            from { opacity: 0; transform: translateY(30px); }
            to { opacity: 1; transform: translateY(0); }
        }

        .splash-logo {
            width: 120px;
            height: auto;
            margin-bottom: 30px;
            animation: splashLogoFloat 3s ease-in-out infinite;
            filter: drop-shadow(0 10px 30px rgba(139, 169, 165, 0.3));
        }

        @keyframes splashLogoFloat {
            0%, 100% { transform: translateY(0); }
            50% { transform: translateY(-15px); }
        }

        .splash-slogan {
            font-family: 'Inter', sans-serif;
            font-size: 28px;
            font-weight: 700;
            color: var(--navy);
            letter-spacing: 2px;
            margin-bottom: 16px;
            opacity: 0;
            animation: textFadeIn 0.8s ease-out 0.5s forwards;
        }

        .splash-tagline {
            font-family: 'Inter', sans-serif;
            font-size: 16px;
            color: var(--sage);
            font-weight: 500;
            letter-spacing: 1px;
            margin-bottom: 24px;
            opacity: 0;
            animation: textFadeIn 0.8s ease-out 0.8s forwards;
        }

        .splash-sustainability {
            display: flex;
            align-items: center;
            justify-content: center;
            gap: 10px;
            font-family: 'Inter', sans-serif;
            font-size: 14px;
            color: var(--text);
            background: white;
            padding: 14px 28px;
            border-radius: 100px;
            box-shadow: 0 8px 30px rgba(139, 169, 165, 0.2);
            opacity: 0;
            animation: textFadeIn 0.8s ease-out 1.1s forwards;
        }

        .splash-sustainability .tree-icon { font-size: 20px; }
        .splash-sustainability strong { color: var(--sage); }

        @keyframes textFadeIn {
            to { opacity: 1; }
        }

        /* Loading Indicator */
        .splash-loader {
            position: absolute;
            bottom: 60px;
            display: flex;
            gap: 8px;
            opacity: 0;
            animation: textFadeIn 0.8s ease-out 1.4s forwards;
        }

        .splash-loader span {
            width: 10px;
            height: 10px;
            background: var(--pink);
            border-radius: 50%;
            animation: loaderBounce 1.4s ease-in-out infinite;
        }

        .splash-loader span:nth-child(2) { animation-delay: 0.2s; background: var(--sage); }
        .splash-loader span:nth-child(3) { animation-delay: 0.4s; background: var(--pink); }

        @keyframes loaderBounce {
            0%, 80%, 100% { transform: scale(0.6); opacity: 0.5; }
            40% { transform: scale(1); opacity: 1; }
        }

        /* Hide main content initially */
        body.loading nav,
        body.loading .hero,
        body.loading main,
        body.loading footer,
        body.loading section {
            opacity: 0;
        }

        body:not(.loading) nav,
        body:not(.loading) .hero,
        body:not(.loading) main,
        body:not(.loading) footer,
        body:not(.loading) section {
            animation: contentFadeIn 0.8s ease-out forwards;
        }

        @keyframes contentFadeIn {
            from { opacity: 0; transform: translateY(20px); }
            to { opacity: 1; transform: translateY(0); }
        }

        /* ============================================
           APP MOCKUPS SECTION
           ============================================ */
        .app-preview {
            padding: 120px 0;
            background: linear-gradient(180deg, #F8F9FA 0%, var(--cream-bg) 100%);
            position: relative;
            overflow: hidden;
        }

        .mockup-scroll-container {
