            gap: 40px;
            min-width: max-content;
            padding: 0 40px;
        }

        .mockup-container {
            display: flex;
            flex-direction: column;
            align-items: center;
            flex-shrink: 0;
            scroll-snap-align: center;
            transition: transform 0.3s ease;
        }

        .mockup-container:hover {
            transform: translateY(-8px);
        }

