    <script>
        // Splash Screen Auto-Hide
        document.addEventListener('DOMContentLoaded', function() {
            const splashScreen = document.getElementById('splashScreen');
            const body = document.body;

            if (splashScreen) {
                setTimeout(function() {
                    splashScreen.classList.add('hidden');
                    body.classList.remove('loading');

                    // Remove splash screen from DOM after animation
                    setTimeout(function() {
                        splashScreen.remove();
                    }, 800);
                }, 3000);
            }
        });

        // Navbar scroll effect
        window.addEventListener('scroll', function() {
            const navbar = document.getElementById('navbar');
            if (window.scrollY > 50) {
                navbar.classList.add('scrolled');
            } else {
                navbar.classList.remove('scrolled');
            }
        });

        // Smooth reveal on scroll
        const observerOptions = {
            threshold: 0.1,
            rootMargin: '0px 0px -50px 0px'
        };

        const observer = new IntersectionObserver((entries) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    entry.target.style.opacity = '1';
                    entry.target.style.transform = 'translateY(0)';
                }
            });
        }, observerOptions);

        document.querySelectorAll('.step-card, .feature-card, .team-card, .pricing-card').forEach(el => {
            el.style.opacity = '0';
            el.style.transform = 'translateY(30px)';
            el.style.transition = 'all 0.6s cubic-bezier(0.175, 0.885, 0.32, 1.275)';
            observer.observe(el);
        });

        // Mockup Carousel Interactivity
        (function() {
            const container = document.querySelector('.mockup-scroll-container');
            const mockups = document.querySelectorAll('.mockup-container');
            const prevBtn = document.getElementById('mockupPrev');
            const nextBtn = document.getElementById('mockupNext');
            const currentScreenEl = document.getElementById('currentScreen');
            const totalScreensEl = document.getElementById('totalScreens');

            if (!container || mockups.length === 0) return;

            let currentIndex = 0;
            const totalScreens = mockups.length;

            // Update total screens display
            if (totalScreensEl) totalScreensEl.textContent = totalScreens;

            // Update screen counter based on scroll position
            function updateCounter() {
                if (!currentScreenEl) return;
                const scrollLeft = container.scrollLeft;
                const containerWidth = container.offsetWidth;
                const scrollWidth = container.scrollWidth - containerWidth;

                if (scrollWidth > 0) {
                    const progress = scrollLeft / scrollWidth;
                    currentIndex = Math.round(progress * (totalScreens - 1));
                    currentScreenEl.textContent = currentIndex + 1;
                }
            }

            // Scroll to specific mockup
            function scrollToMockup(index) {
                if (index < 0) index = 0;
                if (index >= totalScreens) index = totalScreens - 1;

                const mockup = mockups[index];
                if (mockup) {
                    mockup.scrollIntoView({ behavior: 'smooth', inline: 'center', block: 'nearest' });
                    currentIndex = index;
                    if (currentScreenEl) currentScreenEl.textContent = currentIndex + 1;
                }
            }

            // Navigation buttons
            if (prevBtn) {
                prevBtn.addEventListener('click', function() {
                    scrollToMockup(currentIndex - 1);
                });
            }

            if (nextBtn) {
                nextBtn.addEventListener('click', function() {
                    scrollToMockup(currentIndex + 1);
                });
            }

            // Update counter on scroll
            container.addEventListener('scroll', updateCounter);

            // Drag to scroll (mouse)
            let isDown = false;
            let startX;
            let scrollLeft;

            container.addEventListener('mousedown', function(e) {
                isDown = true;
                container.classList.add('is-dragging');
                startX = e.pageX - container.offsetLeft;
                scrollLeft = container.scrollLeft;
            });

            container.addEventListener('mouseleave', function() {
                isDown = false;
                container.classList.remove('is-dragging');
            });

            container.addEventListener('mouseup', function() {
                isDown = false;
                container.classList.remove('is-dragging');
            });

            container.addEventListener('mousemove', function(e) {
                if (!isDown) return;
                e.preventDefault();
                const x = e.pageX - container.offsetLeft;
                const walk = (x - startX) * 2;
                container.scrollLeft = scrollLeft - walk;
            });

            // Keyboard navigation
            document.addEventListener('keydown', function(e) {
                // Only respond when mockup section is in viewport
                const rect = container.getBoundingClientRect();
                const inView = rect.top < window.innerHeight && rect.bottom > 0;

                if (inView) {
                    if (e.key === 'ArrowLeft') {
                        e.preventDefault();
                        scrollToMockup(currentIndex - 1);
                    } else if (e.key === 'ArrowRight') {
                        e.preventDefault();
                        scrollToMockup(currentIndex + 1);
                    }
                }
            });

            // Touch support for better mobile experience
            let touchStartX = 0;
            let touchEndX = 0;

            container.addEventListener('touchstart', function(e) {
                touchStartX = e.changedTouches[0].screenX;
            }, { passive: true });

            container.addEventListener('touchend', function(e) {
                touchEndX = e.changedTouches[0].screenX;
                handleSwipe();
            }, { passive: true });

            function handleSwipe() {
                const swipeThreshold = 50;
                const diff = touchStartX - touchEndX;

                if (Math.abs(diff) > swipeThreshold) {
                    if (diff > 0) {
                        // Swipe left - next
                        scrollToMockup(currentIndex + 1);
                    } else {
                        // Swipe right - prev
                        scrollToMockup(currentIndex - 1);
                    }
                }
            }
        })();
    </script>

    <!-- Sleep Cycle Inspired: Scroll Reveal Script -->
    <script>
        // Intersection Observer for reveal animations
        const revealObserver = new IntersectionObserver((entries) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    entry.target.classList.add('active');
                }
            });
        }, {
            threshold: 0.15,
            rootMargin: '0px 0px -50px 0px'
        });

        // Observe all reveal elements
        document.querySelectorAll('.reveal, .reveal-left, .reveal-right, .reveal-scale').forEach(el => {
            revealObserver.observe(el);
        });

        // Smooth parallax effect for hero shapes
        document.addEventListener('mousemove', (e) => {
            const shapes = document.querySelectorAll('.hero-bg-shapes .shape');
            const moveX = (e.clientX - window.innerWidth / 2) * 0.02;
            const moveY = (e.clientY - window.innerHeight / 2) * 0.02;

            shapes.forEach((shape, i) => {
                const factor = (i + 1) * 0.5;
                shape.style.transform = `translate(${moveX * factor}px, ${moveY * factor}px)`;
            });
        });

        // Enhanced counter animation with easing
        const counterObserver = new IntersectionObserver((entries) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    const target = entry.target;
                    const finalValue = parseInt(target.dataset.count);
                    const duration = 2000;
                    const startTime = performance.now();

                    function easeOutExpo(t) {
                        return t === 1 ? 1 : 1 - Math.pow(2, -10 * t);
                    }

                    function updateCount(currentTime) {
                        const elapsed = currentTime - startTime;
                        const progress = Math.min(elapsed / duration, 1);
                        const easedProgress = easeOutExpo(progress);
                        const currentValue = Math.floor(easedProgress * finalValue);

                        target.textContent = currentValue.toLocaleString() + (target.dataset.suffix || '');

                        if (progress < 1) {
                            requestAnimationFrame(updateCount);
                        }
                    }

                    requestAnimationFrame(updateCount);
                    counterObserver.unobserve(target);
                }
            });
        }, { threshold: 0.5 });

        document.querySelectorAll('[data-count]').forEach(el => {
            counterObserver.observe(el);
        });
    </script>
